	public function getSfNewsAccesss($criteria = null, PropelPDO $con = null)
	{
		if ($criteria === null) {
			$criteria = new Criteria(SfNewsPeer::DATABASE_NAME);
		}
		elseif ($criteria instanceof Criteria)
		{
			$criteria = clone $criteria;
		}

		if ($this->collSfNewsAccesss === null) {
			if ($this->isNew()) {
			   $this->collSfNewsAccesss = array();
			} else {

				$criteria->add(SfNewsAccessPeer::ID_NEWS, $this->id_news);

				SfNewsAccessPeer::addSelectColumns($criteria);
				$this->collSfNewsAccesss = SfNewsAccessPeer::doSelect($criteria, $con);
			}
		} else {
			// criteria has no effect for a new object
			if (!$this->isNew()) {
				// the following code is to determine if a new query is
				// called for.  If the criteria is the same as the last
				// one, just return the collection.


				$criteria->add(SfNewsAccessPeer::ID_NEWS, $this->id_news);

				SfNewsAccessPeer::addSelectColumns($criteria);
				if (!isset($this->lastSfNewsAccessCriteria) || !$this->lastSfNewsAccessCriteria->equals($criteria)) {
					$this->collSfNewsAccesss = SfNewsAccessPeer::doSelect($criteria, $con);
				}
