				$count = SfNewsAccessPeer::doCount($criteria, false, $con);
			}
		} else {
			// criteria has no effect for a new object
			if (!$this->isNew()) {
				// the following code is to determine if a new query is
				// called for.  If the criteria is the same as the last
				// one, just return count of the collection.


				$criteria->add(SfNewsAccessPeer::ID_NEWS, $this->id_news);

				if (!isset($this->lastSfNewsAccessCriteria) || !$this->lastSfNewsAccessCriteria->equals($criteria)) {
					$count = SfNewsAccessPeer::doCount($criteria, false, $con);
				} else {
					$count = count($this->collSfNewsAccesss);
				}
			} else {
				$count = count($this->collSfNewsAccesss);
			}
		}
		return $count;
	}

	/**
	 * Method called to associate a SfNewsAccess object to this object
	 * through the SfNewsAccess foreign key attribute.
	 *
	 * @param      SfNewsAccess $l SfNewsAccess
	 * @return     void
	 * @throws     PropelException
	 */
	public function addSfNewsAccess(SfNewsAccess $l)
	{
		if ($this->collSfNewsAccesss === null) {
			$this->initSfNewsAccesss();
		}
		if (!in_array($l, $this->collSfNewsAccesss, true)) { // only add it if the **same** object is not already associated
			array_push($this->collSfNewsAccesss, $l);
			$l->setSfNews($this);
		}
	}


	/**
	 * If this collection has already been initialized with
	 * an identical criteria, it returns the collection.
	 * Otherwise if this SfNews is new, it will return
	 * an empty collection; or if this SfNews has previously
	 * been saved, it will retrieve related SfNewsAccesss from storage.
	 *
	 * This method is protected by default in order to keep the public
	 * api reasonable.  You can provide public methods for those you
	 * actually need in SfNews.
	 */
	public function getSfNewsAccesssJoinLxProfile($criteria = null, $con = null, $join_behavior = Criteria::LEFT_JOIN)
	{
		if ($criteria === null) {
			$criteria = new Criteria(SfNewsPeer::DATABASE_NAME);
		}
		elseif ($criteria instanceof Criteria)
		{
			$criteria = clone $criteria;
		}

		if ($this->collSfNewsAccesss === null) {
			if ($this->isNew()) {
				$this->collSfNewsAccesss = array();
			} else {

				$criteria->add(SfNewsAccessPeer::ID_NEWS, $this->id_news);

				$this->collSfNewsAccesss = SfNewsAccessPeer::doSelectJoinLxProfile($criteria, $con, $join_behavior);
			}
		} else {
			// the following code is to determine if a new query is
			// called for.  If the criteria is the same as the last
			// one, just return the collection.

			$criteria->add(SfNewsAccessPeer::ID_NEWS, $this->id_news);

			if (!isset($this->lastSfNewsAccessCriteria) || !$this->lastSfNewsAccessCriteria->equals($criteria)) {
				$this->collSfNewsAccesss = SfNewsAccessPeer::doSelectJoinLxProfile($criteria, $con, $join_behavior);
			}
		}
		$this->lastSfNewsAccessCriteria = $criteria;

		return $this->collSfNewsAccesss;
	}

	/**
	 * Resets all collections of referencing foreign keys.
	 *
	 * This method is a user-space workaround for PHP's inability to garbage collect objects
	 * with circular references.  This is currently necessary when using Propel in certain
	 * daemon or large-volumne/high-memory operations.
	 *
	 * @param      boolean $deep Whether to also clear the references on all associated objects.
	 */
	public function clearAllReferences($deep = false)
	{
		if ($deep) {
			if ($this->collSfNewsAccesss) {
				foreach ((array) $this->collSfNewsAccesss as $o) {
					$o->clearAllReferences($deep);
				}
			}
		} // if ($deep)

		$this->collSfNewsAccesss = null;
	}

	// symfony_behaviors behavior
	
	/**
	 * Calls methods defined via {@link sfMixer}.
	 */
	public function __call($method, $arguments)
	{
	  if (!$callable = sfMixer::getCallable('BaseSfNews:'.$method))
	  {
	    throw new sfException(sprintf('Call to undefined method BaseSfNews::%s', $method));
	  }
	
	  array_unshift($arguments, $this);
	
	  return call_user_func_array($callable, $arguments);
	}

} // BaseSfNews
