    // symfony_behaviors behavior
    foreach (sfMixer::getCallables('BaseSfNewsAccessPeer:doInsert:post') as $sf_hook)
    {
      call_user_func($sf_hook, 'BaseSfNewsAccessPeer', $values, $con, $pk);
